import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { forSinglePage } from '../../Api/SinglePageApi';
import parse from 'html-react-parser';
import './Post.css'
import SideNav from '../../Common/SideNav';

const Post = (props) => {
  //console.log(props)
  const [isLoad, setIsLoading] = useState(false);
  const getData = useSelector((state) => state.single.single);
  const isLoading = useSelector((state) => state.single.isActive)
  const dispatch = useDispatch();
  const param = useParams();
  const blogTitle = param.blog

  useEffect(() => {
    singleBlog();
    window.scrollTo(0, 0)

  }, [])

  const singleBlog = async () => {
   await dispatch(SingleArticleAction({ type: "FETCH_SINGLE_DATA_SAGA",payload: blogTitle }))
  }


 return (
    <>

    {isLoading?<LoaderCustom/>:null}
<div className="container marketing">



<SideNav title={getData?.parse?.title} titleforapi={blogTitle}/>

<div className='row single-top'>
    <h2>{getData?.parse?.title}</h2>

    </div>

    <hr className="featurette-divider top-one" />
    <div className='row'>

      {      
    getData && Object.values(getData).map(function(data,i) {
     return(
       <div className='' key={i}>
        {parse(data.text.replace(new RegExp('/wiki/', "g"), ""))}
          </div>
      )

    })
       }
    </div>

    </div>

    </>
  )
}

export default Post
